function null=mark1xx% puts value of 1/x at part marked by mouse clickset(gcf,'WindowButtonDownfcn',[...	'p=get(gca,''CurrentPoint'');',...	'x=num2str(1/p(1,1),4);',...	'y=int2str(1/p(1,2));',...	'space=0;',...	'disp(p(1,1));',...	'text(p(1,1),p(1,2)+space,x,',...			'''HorizontalAlignment'',''center'',',...			'''FontSize'',12,''Color'',''r'')'])